import {React,useState} from 'react';
import TextField from './components/TextField/TextField';
import Button from './components/Button/Button';
import './Bmi.css';
import BmiInputs from './components/BmiInputs/BmiInputs';


function Bmi(){
  const [appName,setAppName] = useState("BMI Tracker");
  const [weight,setWeight] = useState(0);
  const [height,setHeight] = useState(0);
  const [bmi,setBmi] = useState(0);

  const setValues=(e)=>{
    if (e.target.name=="weight"){
      setWeight(e.target.value)
    }else if(e.target.name=="height"){
      setHeight(e.target.value)
    }
  }

  const calcBmi= ()=>{
    console.log(`weight: ${weight},height: ${height}, bmi: ${bmi}`);
    let bmiVal = weight/((height/100)**2);
    setBmi(bmiVal)
  }

  return(
    <div className='container'>
    <TextField 
    textVal={appName} 
    styleClass="text-field"
    />

    <BmiInputs valueHandler={setValues}/>

    <Button
    text="Calculate BMI"
    onClickMethod={calcBmi}
    styleClass="bmi-calc-btn"
    />

    <TextField 
    textVal={<>{bmi.toFixed(4)} <small>Kg/m<sup>2</sup></small></>}
    styleClass="bmi-value-display"
    />
    </div>
  );
}

export default Bmi;
