import {React,useState} from 'react';
import TextField from './components/TextField/TextField';
import Button from './components/Button/Button';
import './Bmi.css';
import BmiInputs from './components/BmiInputs/BmiInputs';


function Bmi(){
  const [appName,setAppName] = useState("BMI Tracker");
  const [width,setWidth] = useState(0);
  const [height,setHeight] = useState(0);

  const setValues=(e)=>{
    if (e.target.name=="width"){
      setWidth(e.target.value)
    }else if(e.target.name=="height"){
      setHeight(e.target.value)
    }
  }
  return(
    <div className='container'>
    <TextField 
    textVal={appName} 
    styleClass="text-field"
    />

    <BmiInputs valueHandler={setValues}/>

    <Button
    text="Calculate BMI"
    onClickMethod={setValues}
    styleClass="bmi-calc-btn"
    />

    <TextField 
    textVal={<>{22.717} <small>Kg/m<sup>2</sup></small></>}
    styleClass="bmi-value-display"
    />
    </div>
  );
}

export default Bmi;
